// check which file extension is requested
$pathinfo = pathinfo($_SERVER['PHP_SELF']);

// file for cleaning up the installer file
$cleanUp = array(
	'/DeleteMe.php' => '',
);
$installerFiles = array_merge($cleanUp, $files);
$document_root = str_replace("\\", "/", dirname(__FILE__));
define("DOCUMENT_ROOT", $document_root);

// Start the session
@session_start();
@session_destroy();

// write the installer files
foreach($installerFiles as $filename => $content) {
	
	// decode the content and change extensions
	$content = str_replace(".php", "." . $pathinfo['extension'], base64_decode($content));
	
	// get the filename with the correct extension
	$filename = str_replace(".php", "." . $pathinfo['extension'], '/OnlineInstaller' . $filename);

	// create needed directories
	$tmp = pathinfo($document_root . $filename);
	$directory = $tmp['dirname'];

	if(!checkMakeDir($directory, 0755)) {
		echo getErrorScreen("An error occured!", "Cannot create directory '$dir'.<br />Please check your file access permissions.<br /><br />In order for webEdition to be installed, the root directory (DOCUMENT_ROOT) must be writable for the web server (Apache, IIS, ..) at least during installation. Please read the installation guide, which is included in the installation package or visit <a href=\"http://www.webedition.de/path/to/write/permission/help.html\" target=\"_blank\">webedition.de</a>.");
		die();
	}

	// open the file which have to be written
	$fp = fopen($document_root . $filename, "wb+");
	if(!$fp) {
		echo getErrorScreen("An error occured!", "Cannot open/create file '$filename'.<br />Please check your file access permissions.<br /><br />In order for webEdition to be installed, the root directory (DOCUMENT_ROOT) must be writable for the web server (Apache, IIS, ..) at least during installation. Please read the installation guide, which is included in the installation package or visit <a href=\"http://www.webedition.de/path/to/write/permission/help.html\" target=\"_blank\">webedition.de</a>.");
		die();
	}

	// put the content into the file
	if($content != "") {
		if(!fputs($fp, $content)) {
			@fclose($fp);
			echo getErrorScreen("An error occured!", "Cannot write file '$filename'.<br />Please check your file access permissions.<br /><br />In order for webEdition to be installed, the root directory (DOCUMENT_ROOT) must be writable for the web server (Apache, IIS, ..) at least during installation. Please read the installation guide, which is included in the installation package or visit <a href=\"http://www.webedition.de/path/to/write/permission/help.html\" target=\"_blank\">webedition.de</a>.");
			die();
		}
	}

	// save the file
	if(!fclose($fp)) {
		echo getErrorScreen("An error occured!", "Cannot save file '$filename'.<br />Please check your file access permissions.<br /><br />In order for webEdition to be installed, the root directory (DOCUMENT_ROOT) must be writable for the web server (Apache, IIS, ..) at least during installation. Please read the installation guide, which is included in the installation package or visit <a href=\"http://www.webedition.de/path/to/write/permission/help.html\" target=\"_blank\">webedition.de</a>.");
		die();
	}

	// chmod
	if(!chmod($document_root . "$filename", 0755)) {
		echo getErrorScreen("An error occured!", "Cannot change file permissions of file '$filename'.");
		die();
	}
}

function checkMakeDir($dirPath, $mod=0755) {
	
	// open_base_dir - seperate document-root from rest
	if (strpos($dirPath, DOCUMENT_ROOT) === 0) {
		$preDir = DOCUMENT_ROOT;
		$dir = substr($dirPath, strlen(DOCUMENT_ROOT));
	} else {
		$preDir = '';
		$dir = $dirPath;
	}

	$pathArray = explode('/', $dir);
	$path = $preDir;

	for($i=0; $i<sizeof($pathArray); $i++) {
		$path .= $pathArray[$i];
		if($pathArray[$i] != "" && !is_dir($path)) {
			if( !(file_exists($path) || mkdir($path, $mod)) ) {
				return false;
			}
		}
		$path .= "/";
	}
	
	if(!chmod($dirPath, $mod)) {
		return false;
	}
	return true;
}

